<?php $woo = 'woocommerce'; ?><tr class="shipping"><th><?php if ( $show_package_details ): ?><?php printf( __( 'Shipping #%d', $woo ), $index + 1 ); ?><?php else: ?><?php print _e( 'Shipping', $woo ); ?><?php endif; ?></th><td><?php if ( !empty( $available_methods ) ): ?><?php if (1 === count($available_methods)): ?><?php $method = current( $available_methods ); ?><?php wp_kses_post( wc_cart_totals_shipping_method_label( $method ) ); ?><input type="hidden" name="shipping_method[<?php print $index; ?>]" data-index="<?php print $index; ?>" id="shipping_method_<?php print $index; ?>" value="<?php print esc_attr( $method->id ); ?>" class="shipping_method"/><?php elseif( get_option( 'woocommerce_shipping_method_format' ) === 'select' ) : ?><select name="shipping_method[<?php print $index; ?>]" data-index="<?php print $index; ?>" id="shipping_method_<?php print $index; ?>" class="shipping_method bootstrap-select"><?php foreach ( $available_methods as $method ): ?><option value="<?php print esc_attr( $method->id ); ?>"><?php print wp_kses_post( wc_cart_totals_shipping_method_label( $method ) ); ?></option><?php endforeach; ?></select><?php else: ?><ul id="shipping_method"><?php foreach ( $available_methods as $method ): ?><li><input type="radio" name="shipping_method[<?php print $index; ?>]" data-index="&lt;?php print $index; ?&gt;" id="shipping_method_<?php print $index; ?>_<?php print sanitize_title( $method->id ); ?>"/><label for="shipping_method_<?php print $index; ?>_<?php print sanitize_title( $method->id ); ?>"></label></li><?php endforeach; ?></ul><?php endif; ?><?php elseif ( !WC()->customer->get_shipping_state() || !WC()->customer->get_shipping_postcode() ): ?><?php if ( is_cart() && get_option( 'woocommerce_enable_shipping_calc' ) === 'yes' ): ?><p><?php _e( 'Please use the shipping calculator to see available shipping methods.', $woo ); ?></p><?php elseif ( is_cart() ): ?><p><?php _e( 'Please continue to the checkout and enter your full address to see if there are any available shipping methods.', $woo ); ?></p><?php else: ?><p><?php _e( 'Please fill in your details to see available shipping methods.', $woo ); ?></p><?php endif; ?><?php else: ?><?php if ( is_cart() ): ?><?php print apply_filters( 'woocommerce_cart_no_shipping_available_html', '<p>' . __( 'There are no shipping methods available. Please double check your address, or contact us if you need any help.', $woo ) . '</p>'); ?><?php else: ?><?php print apply_filters( 'woocommerce_no_shipping_available_html', '<p>' . __( 'There are no shipping methods available. Please double check your address, or contact us if you need any help.', $woo ) . '</p>'); ?><?php endif; ?><?php endif; ?><?php if ( $show_package_details ): ?><?php foreach ( $package['contents'] as $item_id => $values ): ?><?php if ( $values['data']->needs_shipping() ): ?><?php $product_names[] = $values['data']->get_title() . ' &times;' . $values['quantity']; ?><?php print '<p class="woocommerce-shipping-contents"><small>' . __( 'Shipping', 'woocommerce' ) . ': ' . implode( ', ', $product_names ) . '</small></p>'; ?><?php endif; ?><?php endforeach; ?><?php endif; ?><?php if ( is_cart() ) : ?><?php woocommerce_shipping_calculator(); ?><?php endif; ?></td></tr>