<?php global $product, $woocommerce_loop; ?><?php $woo = 'woocommerce'; ?><?php $crosssells = WC()->cart->get_cross_sells(); ?><?php if ( sizeof( $crosssells ) == 0 ) return; ?><?php $meta_query = WC()->query->get_meta_query(); ?><?php $args = array( 'post_type' => 'product', 'ignore_sticky_posts' => 1, 'no_found_rows' => 1, 'posts_per_page'      => apply_filters( 'woocommerce_cross_sells_total', $posts_per_page ), 'orderby' => $orderby, 'post__in' => $crosssells, 'meta_query' => $meta_query ); ?><?php $products = new WP_Query( $args ); ?><?php $woocommerce_loop['columns'] = apply_filters( 'woocommerce_cross_sells_columns', $columns ); ?><?php if ( $products->have_posts() ) : ?><div class="cross-sells"><h2><?php _e( 'You may be interested in&hellip;', $woo ); ?></h2><?php woocommerce_product_loop_start(); ?><?php while ( $products->have_posts() ) : $products->the_post(); ?><?php wc_get_template_part( 'content', 'product' ); ?><?php endwhile; ?><?php woocommerce_product_loop_end(); ?></div><?php endif; ?><?php wp_reset_query(); ?>