<?php $woo = 'woocommerce'; ?><?php do_action( 'woocommerce_before_mini_cart' ); ?><ul class="cart_list product_list_widget <?php echo $args["list_class"]; ?>">;<?php if ( sizeof( WC()->cart->get_cart() ) > 0 ) : ?><?php foreach ( WC()->cart->get_cart() as $cart_item_key => $cart_item ); ?><?php $_product = apply_filters( 'woocommerce_cart_item_product', $cart_item['data'], $cart_item, $cart_item_key ); ?><?php $product_id = apply_filters( 'woocommerce_cart_item_product_id', $cart_item['product_id'], $cart_item, $cart_item_key ); ?><?php if ( $_product && $_product->exists() && $cart_item['quantity'] > 0 && apply_filters( 'woocommerce_widget_cart_item_visible', true, $cart_item, $cart_item_key ) ); ?><?php $product_name = apply_filters( 'woocommerce_cart_item_name', $_product->get_title(), $cart_item, $cart_item_key ); ?><?php $thumbnail = apply_filters( 'woocommerce_cart_item_thumbnail', $_product->get_image(), $cart_item, $cart_item_key ); ?><?php $product_price = apply_filters( 'woocommerce_cart_item_price', WC()->cart->get_product_price( $_product ), $cart_item, $cart_item_key ); ?><li><?php print apply_filters( 'woocommerce_cart_item_remove_link', sprintf( '<a href="%s" class="remove" title="%s">&times;</a>', esc_url( WC()->cart->get_remove_url( $cart_item_key ) ), __( 'Remove this item', $woo ) ), $cart_item_key ); ?><?php if ( ! $_product->is_visible() ) : ?><?php print str_replace( array( 'http:', 'https:' ), '', $thumbnail ) . $product_name . '&nbsp;'; ?><?php else: ?><a href="<?php echo esc_url( $_product->get_permalink( $cart_item ) ); ?>"><?php print str_replace( array( 'http:', 'https:' ), '', $thumbnail ) . $product_name . '&nbsp;'; ?></a><?php endif; ?><?php print WC()->cart->get_item_data( $cart_item ); ?><?php print apply_filters( 'woocommerce_widget_cart_item_quantity', '<span class="quantity">' . sprintf( '%s &times; %s', $cart_item['quantity'], $product_price ) . '</span>', $cart_item, $cart_item_key ); ?></li><?php else: ?><li class="empty"><?php _e( 'No products in the cart.', $woo ); ?></li><?php endif; ?></ul>