<?php $woo = 'woocommerce'; ?><?php if(sizeof(WC()->session->get( 'wc_notices', array())) > 0): ?><div class="col-md-12 cart-alert"><div role="alert" class="alert alert-info alert-dismissible fade in"><button type="button" data-dismiss="alert" aria-label="Close" class="close"><span aria-hidden="true">Ã—</span></button><?php wc_print_notices(); ?></div></div><?php endif; ?><?php do_action( 'woocommerce_before_checkout_form', $checkout ); ?><?php if ( ! $checkout->enable_signup && ! $checkout->enable_guest_checkout && ! is_user_logged_in() ) : ?><?php print apply_filters( 'woocommerce_checkout_must_be_logged_in_message', __( 'You must be logged in to checkout.', $woo ) ); ?><?php return; ?><?php endif; ?><?php $get_checkout_url = apply_filters( 'woocommerce_get_checkout_url', WC()->cart->get_checkout_url() ); ?><div class="col-md-12"><form name="checkout" method="post" action="<?php print esc_url( $get_checkout_url ); ?>" enctype="multipart/form-data" data-toggle="validator" role="form" class="checkout woocommerce-checkout"><div class="row"><div class="col-md-6"><?php if ( sizeof( $checkout->checkout_fields ) > 0 ) : ?><?php do_action( 'woocommerce_checkout_before_customer_details' ); ?><?php do_action( 'woocommerce_checkout_billing' ); ?><?php do_action( 'woocommerce_checkout_shipping' ); ?><?php endif; ?></div><div class="col-md-6"><?php do_action( 'woocommerce_checkout_before_order_review' ); ?><h3 id="order_review_heading"><?php _e( 'Your order', $woo ); ?></h3><div id="order_review" class="woocommerce-checkout-review-order"><?php do_action( 'woocommerce_checkout_order_review' ); ?></div><?php do_action( 'woocommerce_checkout_after_order_review' ); ?></div></div><?php do_action( 'woocommerce_checkout_after_customer_details' ); ?></form></div><?php do_action( 'woocommerce_after_checkout_form', $checkout ); ?>