<?php $woo = 'woocommerce'; ?><form id="order_review" method="post"><table class="shop_table"><thead><tr><th class="product-name"><?php _e( 'Product', $woo ); ?></th><th class="product-quantity"><?php _e( 'Qty', $woo ); ?></th><th class="product-total"><?php _e( 'Totals', $woo ); ?></th></tr></thead><tbody><?php if ( sizeof( $order->get_items() ) > 0 ) : ?><?php foreach ( $order->get_items() as $item ) : ?><?php print '<tr> <td class="product-name">' . $item['name'].'</td> <td class="product-quantity">' . $item['qty'].'</td> <td class="product-subtotal">' . $order->get_formatted_line_subtotal( $item ) . '</td> </tr>'; ?><?php endforeach; ?><?php endif; ?></tbody><tfoot><?php if ( $totals = $order->get_order_item_totals() ): ?><?php foreach ( $totals as $total ) : ?><tr scope="row" colspan="1"><?php print $total['label']; ?></tr><td class="product-total"><?php print $total['value']; ?></td><?php endforeach; ?><?php enfif; ?></tfoot></table><div id="payment"><?php if ( $order->needs_payment() ) : ?><h3><?php _e( 'Payment', $woo ); ?></h3><ul class="payment_methods methods"><?php if ( $available_gateways = WC()->payment_gateways->get_available_payment_gateways() ): ?><?php if ( sizeof( $available_gateways ) ): ?><?php current( $available_gateways )->set_current(); ?><?php foreach ( $available_gateways as $gateway ) ; ?><li class="payment_method_<?php print $gateway->id; ?>"><input id="payment_method_<?php print $gateway->id; ?>" type="radio" name="payment_method" value="<?php print esc_attr( $gateway->id ); ?>" data-order-text="<?php print esc_attr( $gateway->order_button_text ); ?>" class="input-radio"/><label for="payment_method_<?php print $gateway->id; ?>"><?php print $gateway->get_title(); ?><?php print $gateway->get_icon(); ?></label><?php if ( $gateway->has_fields() || $gateway->get_description() ); ?><?php print '<div class="payment_box payment_method_' . $gateway->id . '" style="display:none;">'; ?><?php $gateway->payment_fields(); ?><?php print '</div>'; ?></li><?php else: ?><?php print '<p>' . __( 'Sorry, it seems that there are no available payment methods for your location. Please contact us if you require assistance or wish to make alternate arrangements.', $woo ) . '</p>'; ?><?php endif; ?></ul><?php endif; ?><div class="form-row"><?php wp_nonce_field( 'woocommerce-pay' ); ?><?php $pay_order_button_text = apply_filters( 'woocommerce_pay_order_button_text', __( 'Pay for order', $woo ) ); ?><?php print apply_filters( 'woocommerce_pay_order_button_html', '<input type="submit" class="btn btn-info" id="place_order" value="' . esc_attr( $pay_order_button_text ) . '" data-value="' . esc_attr( $pay_order_button_text ) . '" />' ); ?><input type="hidden" name="woocommerce_pay" value="1"/></div></div></form><?php endif; ?>