<?php $woo = 'woocommerce'; ?><div class="woocommerce-shipping-fields"><?php if ( WC()->cart->needs_shipping_address() === true ) : ?><?php if ( empty( $_POST ) ); ?><?php $ship_to_different_address = get_option( 'woocommerce_ship_to_destination' ) === 'shipping' ? 1 : 0; ?><?php $ship_to_different_address = apply_filters( 'woocommerce_ship_to_different_address_checked', $ship_to_different_address ); ?><?php else: ?><?php $ship_to_different_address = $checkout->get_value( 'ship_to_different_address' ); ?><h3 id="ship-to-different-address"><label for="ship-to-different-address-checkbox" class="checkbox"><?php _e( 'Ship to a different address?', $woo ); ?></label><label id="ship-to-different-address-checkbox" type="checkbox" name="ship_to_different_address" value="1" class="input-checkbox"><?php checked( $ship_to_different_address, 1 ); ?></label></h3><div class="shipping_address"><?php do_action( 'woocommerce_before_checkout_shipping_form', $checkout ); ?><?php foreach ( $checkout->checkout_fields['shipping'] as $key => $field ) : ?><?php woocommerce_form_field( $key, $field, $checkout->get_value( $key ) ); ?><?php endforeach; ?><?php do_action( 'woocommerce_after_checkout_shipping_form', $checkout ); ?></div><?php endif; ?><?php do_action( 'woocommerce_before_order_notes', $checkout ); ?><?php if ( apply_filters( 'woocommerce_enable_order_notes_field', get_option( 'woocommerce_enable_order_comments', 'yes' ) === 'yes' ) ) : ?><?php if ( ! WC()->cart->needs_shipping() || WC()->cart->ship_to_billing_address_only() ) : ?><?php _e( 'Additional Information', $woo ); ?><?php endif; ?><?php foreach ( $checkout->checkout_fields['order'] as $key => $field ) : ?><?php woocommerce_form_field( $key, $field, $checkout->get_value( $key ) ); ?><?php endforeach; ?><?php endif; ?><?php do_action( 'woocommerce_after_order_notes', $checkout ); ?></div>