<?php $woo = 'woocommerce'; ?><div class="table-container"><table class="table shop_table woocommerce-checkout-review-order-table"><thead><tr><th class="product-name"><?php _e( 'Product', $woo ); ?></th><th class="product-total"><?php _e( 'Total', $woo ); ?></th></tr></thead><tbody><?php do_action( 'woocommerce_review_order_before_cart_contents' ); ?><?php foreach ( WC()->cart->get_cart() as $cart_item_key => $cart_item ): ?><?php $_product     = apply_filters( 'woocommerce_cart_item_product', $cart_item['data'], $cart_item, $cart_item_key ); ?><?php if ( $_product && $_product->exists() && $cart_item['quantity'] > 0 && apply_filters( 'woocommerce_checkout_cart_item_visible', true, $cart_item, $cart_item_key ) ); ?><tr class="<?php print esc_attr( apply_filters( "woocommerce_cart_item_class", "cart_item", $cart_item, $cart_item_key ) ); ?>"><td class="product-name"><?php print apply_filters( 'woocommerce_cart_item_name', $_product->get_title(), $cart_item, $cart_item_key ) . '&nbsp;'; ?><?php print apply_filters( 'woocommerce_checkout_cart_item_quantity', ' <strong class="product-quantity">' . sprintf( '&times; %s', $cart_item['quantity'] ) . '</strong>', $cart_item, $cart_item_key ); ?><?php print WC()->cart->get_item_data( $cart_item ); ?></td><td class="product-total"><?php print apply_filters( 'woocommerce_cart_item_subtotal', WC()->cart->get_product_subtotal( $_product, $cart_item['quantity'] ), $cart_item, $cart_item_key ); ?></td></tr><?php do_action( 'woocommerce_review_order_after_cart_contents' ); ?><?php endforeach; ?></tbody><tfoot><tr class="cart-subtotal"><th><?php _e( 'Subtotal', $woo ); ?></th><th><?php wc_cart_totals_subtotal_html(); ?></th></tr><?php foreach ( WC()->cart->get_coupons() as $code => $coupon ) : ?><tr class="cart-discount coupon-<?php print esc_attr( $code ); ?>"><th><?php wc_cart_totals_coupon_label( $coupon ); ?></th><th><?php wc_cart_totals_coupon_html( $coupon ); ?></th></tr><?php endforeach; ?><?php if ( WC()->cart->needs_shipping() && WC()->cart->show_shipping() ) : ?><?php do_action( 'woocommerce_review_order_before_shipping' ); ?><?php wc_cart_totals_shipping_html(); ?><?php do_action( 'woocommerce_review_order_after_shipping' ); ?><?php endif; ?><?php foreach ( WC()->cart->get_fees() as $fee ) : ?><tr class="fee"><?php print esc_html( $fee->name ); ?><?php wc_cart_totals_fee_html( $fee ); ?></tr><?php endforeach; ?><?php if ( WC()->cart->tax_display_cart === 'excl' ) : ?><?php if ( get_option( 'woocommerce_tax_total_display' ) === 'itemized' ) : ?><?php foreach ( WC()->cart->get_tax_totals() as $code => $tax ) : ?><tr class="tax-rate tax-rate-<?php print sanitize_title( $code ); ?>"><th><?php print esc_html( $tax->label ); ?></th><th><?php print wp_kses_post( $tax->formatted_amount ); ?></th></tr><?php endforeach; ?><?php else: ?><tr class="tax-total"><th><?php print esc_html( WC()->countries->tax_or_vat() ); ?></th><th><?php print wc_price( WC()->cart->get_taxes_total() ); ?></th></tr><?php endif; ?><?php endif; ?><?php do_action( 'woocommerce_review_order_before_order_total' ); ?><tr class="order-total"><th><?php print _e( 'Total', $woo ); ?></th><th><?php wc_cart_totals_order_total_html(); ?></th></tr><?php do_action( 'woocommerce_review_order_after_order_total' ); ?></tfoot></table></div>