<?php $woo = 'woocommerce'; ?><?php if ( $order ) : ?><div class="col-md-12"><?php if ( $order->has_status( 'failed' ) ) : ?><div role="alert" class="alert alert-danger alert-dismissible fade in"><button type="button" data-dismiss="alert" aria-label="Close" class="close"><span aria-hidden="true">×</span></button><span><?php print __( 'Unfortunately your order cannot be processed as the originating bank/merchant has declined your transaction.', $woo ); ?></span><span><?php if ( is_user_logged_in() ): ?><print>__( 'Please attempt your purchase again or go to your account page.', $woo );</print><?php else: ?><print>__( 'Please attempt your purchase again.', $woo );</print><?php endif; ?></span><span><a href="<?php print esc_url( $order->get_checkout_payment_url() ); ?>" class="button pay btn btn-info"><?php print __( 'Pay', $woo ) ?></a><?php if ( is_user_logged_in() ) : ?><a href="<?php print esc_url( wc_get_page_permalink( "myaccount" ) ); ?>" class="button pay btn btn-info"><?php print __( 'My Account', $woo ); ?></a><?php endif; ?></span></div><?php else: ?><div role="alert" class="alert alert-info alert-dismissible fade in"><button type="button" data-dismiss="alert" aria-label="Close" class="close"><span aria-hidden="true">×</span></button><span><?php print apply_filters( 'woocommerce_thankyou_order_received_text', __( 'Thank you. Your order has been received.', $woo ), $order ); ?></span></div><table class="table table-hover"><tr><td><?php _e( 'Order Number:', $woo ); ?></td><td><strong><?php print $order->get_order_number(); ?></strong></td></tr><tr><td><?php _e( 'Date:', $woo ); ?></td><td><strong><?php print date_i18n( get_option( 'date_format' ), strtotime( $order->order_date ) ); ?></strong></td></tr><tr><td><?php _e( 'Total:', $woo ); ?></td><td><strong><?php print $order->get_formatted_order_total(); ?></strong></td></tr><?php if ( $order->payment_method_title ) : ?><tr class="hidden"><td><?php _e( 'Payment Method:', $woo ); ?></td><td><strong><?php print $order->payment_method_title; ?></strong></td></tr><?php endif; ?></table><?php endif; ?><div class="hidden"><?php do_action( 'woocommerce_thankyou_' . $order->payment_method, $order->id ); ?></div><div class="row"><?php do_action( 'woocommerce_thankyou', $order->id ); ?></div></div><?php else: ?><div class="col-md-12"><div role="alert" class="alert alert-info alert-dismissible fade in"><button type="button" data-dismiss="alert" aria-label="Close" class="close"><span aria-hidden="true">×</span></button><span><?php print apply_filters( 'woocommerce_thankyou_order_received_text', __( 'Thank you. Your order has been received.', $woo ), null ); ?></span></div></div><?php endif; ?>