<!DOCTYPE html><html><head><?php get_header(); ?></head><body><section class="slider head-slider"><div id="monCarousel" data-ride="carousel" class="carousel slide"><ol class="carousel-indicators"><?php global $wpdb; ?><?php $slides_count = $wpdb->get_var('SELECT COUNT(*) FROM wp_posts AS p WHERE p.post_type="slide" AND p.post_status = "publish"'); ?><?php for($i=0; $i<$slides_count; $i++): ?><?php if($i==0): ?><li data-target="#monCarousel" data-slide-to="<?php print($i); ?>" class="active"></li><?php else: ?><li data-target="#monCarousel" data-slide-to="<?php print($i); ?>"></li><?php endif; ?><?php endfor; ?></ol><div role="listbox" class="carousel-inner"><?php $slider = new WP_Query( array ( 'post_type' => 'slide')); ?><?php if ( $slider->have_posts() ): ?><?php while ( $slider->have_posts() ): ?><?php $slider->the_post(); ?><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('slide', '1920x600', $image_id); ?><div style="background-image:url(<?php print $image_src; ?>" class="item"><img src="<?php print $image_src; ?>" class="visible-xs background-img"><div class="container"><h2><?php the_excerpt(); ?></h2></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div><a href="#monCarousel" role="button" data-slide="prev" class="left carousel-control hidden-xs"><span aria-hidden="true" class="glyphicon glyphicon-chevron-left"></span><span class="sr-only">Previous</span></a><a href="#monCarousel" role="button" data-slide="next" class="right carousel-control hidden-xs"><span aria-hidden="true" class="glyphicon glyphicon-chevron-right"></span><span class="sr-only">Next</span></a></div></section><section class="header"><div class="container"><div class="row"><div class="col-md-9 header-menu"><div role="navigation" class="navbar navbar-default"><div class="navbar-header"><button type="button" data-toggle="collapse" data-target=".navbar-collapse" class="navbar-toggle"><span class="sr-only">Toggle navigation</span><span class="icon-bar"></span><span class="icon-bar"></span><span class="icon-bar"></span></button><a href="/" class="navbar-brand"><span><img src="<?php echo get_template_directory_uri(); ?>/assets/img/logo.png" alt=""></span></a></div><div class="navbar-collapse collapse"><ul class="nav navbar-nav"><?php if (class_exists('wpMenu', false)): ?><?php $wpM = new wpMenu('top-menu'); ?><?php $firstMenuLevel = $wpM->getMenu(0); ?><?php foreach($firstMenuLevel as $menu_item_0): ?><li class="<?php print($menu_item_0->class." ".$menu_item_0->megaclass); ?>"><a href="<?php print($menu_item_0->link); ?>" title="<?php print($menu_item_0->title); ?>"><?php print($menu_item_0->title); ?></a></li><?php endforeach; ?><?php endif; ?></ul></div></div></div><div class="col-md-3 header-sidebar text-right"><?php $fb = get_option('ma_fb'); ?><?php if(!empty($fb)): ?><a href="<?php print($fb); ?>" class="social-link facebook"><i class="fa fa-facebook"></i></a><?php endif; ?><?php $vk = get_option('ma_vk'); ?><?php if(!empty($vk)): ?><a href="<?php print($vk); ?>" class="social-link vk"><i class="fa fa-vk"></i></a><?php endif; ?><?php $pi = get_option('ma_pi'); ?><?php if(!empty($pi)): ?><a href="<?php print($pi); ?>" class="social-link pinterest"><i class="fa fa-pinterest"></i></a><?php endif; ?><?php $im = get_option('ma_im'); ?><?php if(!empty($im)): ?><a href="<?php print($im); ?>" class="social-link instagram"><i class="fa fa-instagram"></i></a><?php endif; ?></div><div class="col-md-12 hidden-sm bottom-line-col"><div class="bottom-line"></div></div></div></div></section><section class="content"><div class="container"><div class="row"><div class="col-md-9 col-sm-12 main-content"><div class="categories-content"><div class="row category-blocks"><?php if (class_exists('wpMenu', false)): ?><?php $wpM = new wpMenu('home-menu'); ?><?php $firstMenuLevel = $wpM->getMenu(0); ?><?php $i = 1; ?><?php foreach($firstMenuLevel as $menu_item_0): ?><?php $image_id = $menu_item_0->thumbnail; ?><?php $image_src = getImage('blocks', '430x300', $image_id); ?><?php if(!empty($menu_item_0->title) && !empty($image_src)): ?><div class="col-md-6 col-sm-6 category-block"><a href="<?php print($menu_item_0->link); ?>" title="<?php print($menu_item_0->title); ?>" class="<?php print($menu_item_0->class." ".$menu_item_0->megaclass); ?>"><img src="<?php print($image_src); ?>" alt="<?php print($menu_item_0->title); ?>"><span><?php print($menu_item_0->title); ?></span></a></div><?php if($i%2 == 0 && $i!=sizeof($firstMenuLevel)): ?><?php print('</div><div class="row category-blocks">'); ?><?php endif; ?><?php $i++; ?><?php endif; ?><?php endforeach; ?><?php endif; ?></div></div><div class="double-border-bottom"></div></div><div class="col-md-3 col-sm-12 sidebar"><?php $post_type = get_post_type(); ?><?php if( is_tax('product_cat') || is_single() && $post_type == 'product' || is_post_type_archive('product')): ?><?php global $themename; ?><?php global $themename, $woocommerce; ?><?php $cart_content = $woocommerce->cart->get_cart(); ?><aside class="cart"><div class="aside-container"><div class="aside-header"><h3 class="pull-left"><a href="<?php print($woocommerce->cart->get_cart_url()); ?>"><?php print(__('Cart', $themename)); ?></a></h3></div><div class="aside-content header-content cart-content"><table class="table"><thead><tr><td><?php print(__('Product', $themename)); ?></td><td><?php print(__('Qt-ity', $themename)); ?></td><td><?php print(__('Price', $themename)); ?></td></tr></thead><tbody class="cart-product-list"><?php foreach($cart_content as $index=>$product): ?><tr class="cart-product"><td><a data-product_id="<?php print($product["product_id"]); ?>" data-action="remove_product" data-quantity="1" data-key="<?php print($index); ?>" href="#" class="cart-operation remove"><i class="fa fa-times"></i></a><span class="product-cart-name"><?php print($product['data']->post->post_title); ?></span></td><td><span class="product-cart-quantity"><?php print($product['quantity']); ?></span></td><td><?php print(wc_price($product['line_total'])); ?></td></tr><?php endforeach; ?></tbody><tfoot><tr><td><span><?php print(__('Products:', $themename)); ?>&nbsp;</span><span class="cart-total-count"><?php print($woocommerce->cart->cart_contents_count); ?></span></td><td colspan="2"><span><?php print(__('Total:', $themename)); ?>&nbsp;</span><span class="cart-total-price"><?php print($woocommerce->cart->get_cart_total()); ?></span></td></tr></tfoot></table><?php $cart_class = 'row cart-buttons-row '; ?><?php if(sizeof( $woocommerce->cart->cart_contents) == 0): ?><?php $cart_class .= 'hidden'; ?><?php endif; ?><div class="<?php print($cart_class); ?>"><div class="col-md-6 text-right"><a href="<?php print($woocommerce->cart->get_checkout_url()); ?>" class="btn btn-default text-center"><?php print(__('Submit order', $themename)); ?></a></div><div class="col-md-6 text-left"><a href="<?php print($woocommerce->cart->get_cart_url()); ?>" class="btn btn-default text-center"><?php print(__('Go to cart', $themename)); ?></a></div></div></div></div></aside><?php endif; ?><aside class="news"><?php global $sidebar_excerpt_words; ?><?php $category_id = get_category_by_slug( 'news' ); ?><?php $news_link = get_category_link($category_id); ?><div class="aside-container"><div class="aside-header"><h3 class="pull-left"><a href="<?php print esc_url($news_link); ?>"><?php print($category_id->cat_name); ?></a></h3><div class="read-more-aside pull-right text-right"><a href="<?php print esc_url($news_link); ?>"><?php print(__('Show all', $themename)); ?></a></div><!--.btn-container.pull-right<a class="btn btn-primary"><span>Подписаться</span><i class="fa fa-envelope-o"></i></a>--></div><div class="aside-content header-content"><?php $news = new WP_Query(array('category_name' => 'news','showposts' => 1)); ?><?php if ( $news->have_posts() ): ?><?php while ( $news->have_posts() ): ?><?php $news->the_post(); ?><div class="row news-row"><div class="col-md-5 img-block hidden-sm hidden-xs"><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('group', '200x200', $image_id); ?><a href="<?php print(get_the_permalink()); ?>"><img src="<?php print($image_src); ?>" alt="<?php print(get_the_title()); ?>"></a></div><div class="col-md-7 text-block"><a href="<?php print(get_the_permalink()); ?>" class="title"><?php print(get_the_title()); ?></a><p class="excerpt"><?php print(wp_trim_words(get_the_excerpt(), $sidebar_excerpt_words)); ?></p></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div></div></aside><aside class="news actions"><?php global $sidebar_excerpt_words; ?><?php $category_id = get_category_by_slug( 'actions' ); ?><?php $actions_link = get_category_link($category_id); ?><div class="aside-container"><div class="aside-header"><h3 class="pull-left"><a href="<?php print esc_url($actions_link); ?>"><?php print($category_id->cat_name); ?></a></h3><div class="read-more-aside pull-right text-right"><a href="<?php print esc_url($actions_link); ?>"><?php print(__('Show all', $themename)); ?></a></div></div><div class="aside-content header-content"><?php $news = new WP_Query(array('category_name' => 'actions','showposts' => 1)); ?><?php if ( $news->have_posts() ): ?><?php while ( $news->have_posts() ): ?><?php $news->the_post(); ?><div class="row news-row"><div class="col-md-12 img-block hidden-sm hidden-xs"><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('actions', '270x100', $image_id); ?><a href="<?php print(get_the_permalink()); ?>"><img src="<?php print($image_src); ?>" alt="<?php print(get_the_title()); ?>"></a></div><div class="col-md-12 text-block"><a href="<?php print(get_the_permalink()); ?>" class="title"><?php print(get_the_title()); ?></a><p class="excerpt"><?php print(wp_trim_words(get_the_excerpt(), $sidebar_excerpt_words)); ?></p></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div></div></aside><aside class="news"><?php global $sidebar_excerpt_words; ?><?php $category_id = get_category_by_slug( 'articles' ); ?><?php $articles_link = get_category_link($category_id); ?><div class="aside-container"><div class="aside-header"><h3 class="pull-left"><a href="<?php print esc_url($articles_link); ?>"><?php print($category_id->cat_name); ?></a></h3><div class="read-more-aside pull-right text-right"><a href="<?php print esc_url($articles_link); ?>"><?php print(__('Show all', $themename)); ?></a></div></div><div class="aside-content header-content"><?php $news = new WP_Query(array('category_name' => 'articles','showposts' => 2)); ?><?php if ( $news->have_posts() ): ?><?php while ( $news->have_posts() ): ?><?php $news->the_post(); ?><div class="row news-row"><div class="col-md-5 img-block hidden-sm hidden-xs"><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('articles', '100x80', $image_id); ?><a href="<?php print(get_the_permalink()); ?>"><img src="<?php print($image_src); ?>" alt="<?php print(get_the_title()); ?>"></a></div><div class="col-md-7 text-block"><a href="<?php print(get_the_permalink()); ?>" class="title"><?php print(get_the_title()); ?></a><p class="excerpt"><?php print(wp_trim_words(get_the_excerpt(), $sidebar_excerpt_words)); ?></p></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div></div></aside><aside class="news"><?php $literarure_url = get_post_type_archive_link( 'literature' ); ?><?php $post_type = get_post_type_object('literature'); ?><div class="aside-container"><div class="aside-header"><h3 class="pull-left"><a href="<?php print esc_url($literarure_url); ?>"><?php print($post_type->labels->name); ?></a></h3><div class="read-more-aside pull-right text-right"><a href="<?php print esc_url($literarure_url); ?>"><?php print(__('Show all', $themename)); ?></a></div></div><div class="aside-content header-content"><?php query_posts( array ( 'post_type' => 'literature', 'showposts' => 2)); ?><?php if (have_posts()): ?><?php while (have_posts()): ?><?php the_post(); ?><div class="row news-row"><div class="col-md-5 img-block hidden-sm hidden-xs"><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('articles', '100x80', $image_id); ?><a href="<?php print(get_the_permalink()); ?>"><img src="<?php print($image_src); ?>" alt="<?php print(get_the_title()); ?>"></a></div><div class="col-md-7 text-block"><a href="<?php print(get_the_permalink()); ?>" class="title"><?php print(get_the_title()); ?></a><p class="excerpt"><?php $author = get_post_meta($post->ID,'ma_author', true); ?><?php print($author); ?></p></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div></div></aside></div></div></div></section><?php get_footer(); ?></body></html>