<?php global $themename, $woocommerce, $product; ?><!DOCTYPE html><html><head><?php get_header(); ?></head><body><section class="slider head-slider"><div id="monCarousel" data-ride="carousel" class="carousel slide"><ol class="carousel-indicators"><?php global $wpdb; ?><?php $slides_count = $wpdb->get_var('SELECT COUNT(*) FROM wp_posts AS p WHERE p.post_type="slide" AND p.post_status = "publish"'); ?><?php for($i=0; $i<$slides_count; $i++): ?><?php if($i==0): ?><li data-target="#monCarousel" data-slide-to="<?php print($i); ?>" class="active"></li><?php else: ?><li data-target="#monCarousel" data-slide-to="<?php print($i); ?>"></li><?php endif; ?><?php endfor; ?></ol><div role="listbox" class="carousel-inner"><?php $slider = new WP_Query( array ( 'post_type' => 'slide')); ?><?php if ( $slider->have_posts() ): ?><?php while ( $slider->have_posts() ): ?><?php $slider->the_post(); ?><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('slide', '1920x600', $image_id); ?><div style="background-image:url(<?php print $image_src; ?>" class="item"><img src="<?php print $image_src; ?>" class="visible-xs background-img"><div class="container"><h2><?php the_excerpt(); ?></h2></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div><a href="#monCarousel" role="button" data-slide="prev" class="left carousel-control hidden-xs"><span aria-hidden="true" class="glyphicon glyphicon-chevron-left"></span><span class="sr-only">Previous</span></a><a href="#monCarousel" role="button" data-slide="next" class="right carousel-control hidden-xs"><span aria-hidden="true" class="glyphicon glyphicon-chevron-right"></span><span class="sr-only">Next</span></a></div></section><section class="header"><div class="container"><div class="row"><div class="col-md-9 header-menu"><div role="navigation" class="navbar navbar-default"><div class="navbar-header"><button type="button" data-toggle="collapse" data-target=".navbar-collapse" class="navbar-toggle"><span class="sr-only">Toggle navigation</span><span class="icon-bar"></span><span class="icon-bar"></span><span class="icon-bar"></span></button><a href="/" class="navbar-brand"><span><img src="<?php echo get_template_directory_uri(); ?>/assets/img/logo.png" alt=""></span></a></div><div class="navbar-collapse collapse"><ul class="nav navbar-nav"><?php if (class_exists('wpMenu', false)): ?><?php $wpM = new wpMenu('top-menu'); ?><?php $firstMenuLevel = $wpM->getMenu(0); ?><?php foreach($firstMenuLevel as $menu_item_0): ?><li class="<?php print($menu_item_0->class." ".$menu_item_0->megaclass); ?>"><a href="<?php print($menu_item_0->link); ?>" title="<?php print($menu_item_0->title); ?>"><?php print($menu_item_0->title); ?></a></li><?php endforeach; ?><?php endif; ?></ul></div></div></div><div class="col-md-3 header-sidebar text-right"><?php $fb = get_option('ma_fb'); ?><?php if(!empty($fb)): ?><a href="<?php print($fb); ?>" class="social-link facebook"><i class="fa fa-facebook"></i></a><?php endif; ?><?php $vk = get_option('ma_vk'); ?><?php if(!empty($vk)): ?><a href="<?php print($vk); ?>" class="social-link vk"><i class="fa fa-vk"></i></a><?php endif; ?><?php $pi = get_option('ma_pi'); ?><?php if(!empty($pi)): ?><a href="<?php print($pi); ?>" class="social-link pinterest"><i class="fa fa-pinterest"></i></a><?php endif; ?><?php $im = get_option('ma_im'); ?><?php if(!empty($im)): ?><a href="<?php print($im); ?>" class="social-link instagram"><i class="fa fa-instagram"></i></a><?php endif; ?></div><div class="col-md-12 hidden-sm bottom-line-col"><div class="bottom-line"></div></div></div></div></section><section class="content"><div class="container"><div class="row"><div class="col-md-9 main-content"><?php global $themename; ?><div class="row page-head"><div class="col-md-12"><?php if (class_exists('wpBreadcrumbs', false)): ?><?php $BC = new wpBreadcrumbs('<i class="fa fa-home"></i>','<i class="fa fa-angle-right"></i>',$post_types = array('decoration'=>'decoration-type','product'=>'product_cat','master'=>'master-type','project'=>'design-type')); ?><?php $breadcrumbs = $BC->getBreadcrumbs(); ?><?php if(count($breadcrumbs) > 0): ?><div class="breadcrumbs hidden-xs"><h1 itemscope itemtype="http://data-vocabulary.org/Breadcrumb" class="page-title"><a href="<?php print($breadcrumbs[0]['link']); ?>" itemprop="url"><span class="icon"><?php print($breadcrumbs[0]['icon']); ?></span><span itemprop="title"><?php print($breadcrumbs[0]['text']); ?></span></a><?php unset($breadcrumbs[0]); ?><?php foreach($breadcrumbs as $breadcrumb): ?><?php if($breadcrumb['text']): ?><span itemprop="child" itemscope itemtype="http://data-vocabulary.org/Breadcrumb"><?php if(!empty($breadcrumb['link'])): ?><a href="<?php print($breadcrumb['link']); ?>" itemprop="url"><span class="icon"><?php print($breadcrumb['icon']); ?></span><span itemprop="title"><?php print($breadcrumb['text']); ?></span></a><?php else: ?><span class="icon"><?php print($breadcrumb['icon']); ?></span><span itemprop="title"><?php print($breadcrumb['text']); ?></span><?php endif; ?></span><?php endif; ?><?php endforeach; ?></h1></div><?php endif; ?><?php endif; ?><?php if(is_tax('product_cat')): ?><?php $cid = get_queried_object_id(); ?><?php $args = array('orderby' => 'name','order' => 'ASC','hide_empty' => true, 'exclude' => array(),'number' => '','fields' => 'all','parent'  => $cid,'hierarchical' => false,'child_of' => $cid, 'offset' => ''); ?><?php $terms = get_terms('product_cat', $args); ?><?php $display_type = get_woocommerce_term_meta( $cid, 'display_type', true ); ?><?php if(empty($terms) || sizeof($terms)<1 || $display_type == 'products'): ?><div class="product-filters"><div class="product-filter"><a href="#" data-filter="*" class="btn btn-default"><?php print(__('All products', $themename)); ?></a></div><div class="product-filter"><a href="#" data-filter=".sale" class="btn btn-default"><?php print(__('Sale products', $themename)); ?></a></div></div><?php endif; ?><?php endif; ?><div class="double-border-bottom"></div></div></div><div class="row"><div itemscope itemtype="http://schema.org/Product" class="page-line col-md-12 product-info"><div class="page-container pull-left"><?php do_action( 'woocommerce_before_single_product' ); ?><?php $product_brand = array_shift(wc_get_product_terms($post->ID, 'pa_proizvoditel',array( 'fields' => 'names' ))); ?><?php $attributes = $product->get_attributes(); ?><?php $availability = $product->get_availability(); ?><?php $price = $product->get_regular_price(); ?><?php $sale_price = $product->get_sale_price(); ?><?php $regular_price = $product->get_regular_price(); ?><div class="page-heading"><h3><span itemprop="name"><?php print(get_the_title()); ?></span></h3></div><div class="page-content pull-left"><div class="product-common"><div class="col-md-6 product-big-picture"><?php $post_image = getPostImage(); ?><?php $image_id = get_post_thumbnail_id(); ?><?php $image_src = getImage('decoration', '400x210', $image_id); ?><?php $thumbnail_src = getImage('group', '200x200', $image_id); ?><?php if($post_image): ?><a href="<?php print($post_image); ?>" data-options="thumbnail:'<?php print($thumbnail_src); ?>'" class="lightbox"><img src="<?php print($image_src); ?>" alt="<?php print(get_the_title()); ?>" itemprop="image" class="alignnone size-full img-responsive"></a><?php endif; ?><?php $pgallery = $product->get_gallery_attachment_ids(); ?><?php if(!empty($pgallery) && sizeof($pgallery) > 0): ?><div class="row product-gallery"><?php foreach($pgallery as $gid): ?><?php $pthumbnail_src = getImage('group', '200x200', $gid); ?><?php $pfull_src = wp_get_attachment_image_src($gid, 'full'); ?><div class="col-md-3 gallery-small-pictures-col"><?php if(!empty($pfull_src)): ?><a href="<?php print($pfull_src[0]); ?>" data-options="thumbnail:'<?php print($pthumbnail_src); ?>'" rel="ilightbox[gallery<?php print(get_the_ID()); ?>]" class="lightbox"><img src="<?php print($pthumbnail_src); ?>" alt="<?php print(get_the_title()); ?>" class="img-responsive"></a><?php else: ?><a><img src="<?php print($pthumbnail_src); ?>" alt="<?php print(get_the_title()); ?>" class="img-responsive"></a><?php endif; ?></div><?php endforeach; ?></div><?php endif; ?></div><div class="col-md-6 product-info"><span itemprop="offers" itemscope itemtype="http://schema.org/Offer"><p class="aviability"><link itemprop="availability" href="http://schema.org/InStock"><?php if(!empty($availability['availability'])): ?><span class="label label-danger <?php print($availability["class"]); ?>"><?php print($availability['availability']); ?></span><?php else: ?><span class="in-stock label label-success"><?php print(__('In stock', $themename)); ?></span><?php endif; ?></p><p class="regular-price"><span><?php print(__('Regular price:', $themename)) ?></span><span itemprop="price"><?php print(wc_price($regular_price)); ?></span></p><meta itemprop="priceCurrency" content="<?php print(get_woocommerce_currency()); ?>"><?php if(!empty($sale_price)): ?><p class="sale-price"><span><?php print(__('Sale price:', $themename)) ?></span><span itemprop="price"><?php print(wc_price($sale_price)); ?></span></p><?php endif; ?><?php if(!empty($attributes)): ?><table class="table"><?php foreach ( $attributes as $attribute ) : ?><?php if ( empty( $attribute['is_visible'] ) || ( $attribute['is_taxonomy'] && ! taxonomy_exists( $attribute['name'] ) ) ): ?><?php continue; ?><?php else: ?><?php $has_row = true; ?><?php endif; ?><tr class="<?php if ( ( $alt = $alt * -1 ) == 1 ) print('alt'); ?>"><td><?php print(wc_attribute_label( $attribute['name'] )); ?><?php print(': ') ?></td><td><?php if ( $attribute['is_taxonomy'] ) : ?><?php $values = wc_get_product_terms( $product->id, $attribute['name'], array( 'fields' => 'names' ) ); ?><?php print(str_replace( "\n", "",strip_tags(apply_filters( 'woocommerce_attribute', wpautop( wptexturize( implode( ', ', $values ) ) ), $attribute, $values )))); ?><?php else: ?><?php $values = array_map( 'trim', explode( WC_DELIMITER, $attribute['value'] ) ); ?><?php print(strip_tags(apply_filters( 'woocommerce_attribute', wpautop( wptexturize( implode( ', ', $values ) ) ), $attribute, $values ))); ?><?php endif; ?></td></tr><?php endforeach; ?></table><?php endif; ?></span><div class="btn-container"><a data-product_id="<?php print($product->id); ?>" data-action="add_product" data-quantity="1" data-key="" href="#" class="btn btn-info cart-operation add"><?php print(__('Add to cart', $themename)); ?></a></div><span itemprop="description" class="hidden"><?php print(get_the_excerpt()); ?></span></div></div><div class="product-description"><div class="col-md-12"><div class="page-text"><?php the_content(); ?></div></div></div></div></div></div><?php wp_reset_query(); ?><?php $upsels = $product->get_upsells(); ?><?php if(sizeof($upsels) > 0): ?><?php query_posts(array('post__in' => $upsels,'post_type'=> 'product')); ?><?php if (have_posts()): ?><?php $carousel_title = __('Related products',$themename); ?><div class="col-md-12 double-border-container"><div class="double-border-bottom"></div></div><div class="page-line col-md-12 interior-carousel"><div class="page-container pull-left"><div class="page-heading"><?php if(!empty($carousel_title)): ?><h3><?php print($carousel_title); ?></h3><?php endif; ?><?php if(!empty($button_text)): ?><div class="btn-container page-heading-order"><?php if(substr($button_link, 0, 1) == '#'): ?><a data-toggle="modal" data-target="<?php print($button_link); ?>" class="btn btn-primary"><span><?php print($button_text); ?></span><i class="fa fa-chevron-circle-right"></i></a><?php else: ?><a href="<?php print($button_link); ?>" class="btn btn-primary"><span><?php print($button_text); ?></span><i class="fa fa-chevron-circle-right"></i></a><?php endif; ?></div><?php endif; ?></div><div class="page-content pull-left"><div class="page-text"><div class="carousel carousel-block"><div class="carousel-frame"><ul class="carousel-slider"><?php while (have_posts()): ?><?php the_post(); ?><?php $image