<?php print(get_the_permalink()); ?>"><img src="<?php print($image_src); ?>" alt="<?php print(get_the_title()); ?>"></a></div><div class="col-md-7 text-block"><a href="<?php print(get_the_permalink()); ?>" class="title"><?php print(get_the_title()); ?></a><p class="excerpt"><?php $author = get_post_meta($post->ID,'ma_author', true); ?><?php print($author); ?></p></div></div><?php endwhile; ?><?php endif; ?><?php wp_reset_query(); ?></div></div></aside></div></div></div></section><?php get_footer(); ?></body></html>